import React, { Component } from "react";
import PageTemplate from "./PageTemplate";
import TodoInput from "./TodoInput";
import TodoList from "./TodoList";

class App extends Component {
	state = {
		input : "",
		todoList : [
			{
				idx : 0,
				flag : false,
				msg : "어렵다 리액트"
			},
			{
				idx : 1,
				flag : true,
				msg : "안보고 짤 수 있을때까지....."
			}
		]
	};

	getIndex = () => {
		console.log(this.state.todoList);
		const list = this.state.todoList;
		let a = 0;
		list.map(
			todo => (
				a = todo.idx
			)
		)

		return ++a;
	}

	handleChange = (e) => {
		/*
		* 변수 선언을 하려면 변수명을 value로 지정해야 e.target.value와 매핑됨.
		* 변수 선언 없이 e.target.value로 넣어도 무방함.
		* */
		const {value} = e.target;
		this.setState({
			input : value
		});
	};

	handleInsert = () => {
		/* input에 입력한 값을 msg에 넣어야하므로 input으로 변수 선언(this.state.input) 후 신규 msg에 넣어줌. */
		const {input, todoList} = this.state;
		const newTodo = {
			idx : this.getIndex(),
			flag : false,
			msg : input
		}

		this.setState({
			todoList : [
				...todoList,
				newTodo
			]
		});
	};

	render(){
		return (
			<div>
				<PageTemplate>
					<TodoInput onChange={this.handleChange} value={this.state.input} todoInsert={this.handleInsert}/>
					<TodoList todoList={this.state.todoList}/>
				</PageTemplate>
			</div>
		)
	}
}

export default App;
